Mat trainLableMat(803, 1, CV_32FC1, trainLabels);
Mat testLableMat(778, 1, CV_32FC1, testLabels);

int main(int argc, char** argv)
{
	Mat frame, maskFrame;
	Mat featureTrainMatrix, featureTestMatrix;

	//***************************************************Loading Pictures**********************************************************//
	//*****************************************************************************************************************************//
	VideoCapture cap(imagePath); // image sequence
	if (!cap.isOpened())  // check if we succeeded
		return -1;

	for (int i = 1;  ; i++) {
		cap >> frame;
		if (frame.empty()) break;
		
		stringstream ss;
		if (i < 10) {
			ss << "HEp-2_cells\\CellsGT\\0" << i << cellsGTImg;
		} else { ss << "HEp-2_cells\\CellsGT\\" << i << cellsGTImg; }
		cellsGT = ss.str();


		//***************************************************Loading Cell Masks********************************************************//
		//*****************************************************************************************************************************//
		VideoCapture capMask(cellsGT);
		cout << endl <<"Picture " << i << " calculating...";
		for (int j = 1; ; j++) {
			capMask >> maskFrame;
			if (maskFrame.empty()) break;

			//calculate featureMatrix
			if (elementFound(i, trainIndex, 15) == true) {
				featureTrainMatrix.push_back(calcFeatureVec(frame, maskFrame));
			} else 	featureTestMatrix.push_back(calcFeatureVec(frame, maskFrame));

		}
	}

	/************************************Load & Save*****************************************************************/
	/****************************************************************************************************************/

//	featureTrainMatrix = loadFile("TrainMatrix");
//	featureTestMatrix = loadFile("TestMatrix");

	//cout << featureTrainMatrix << endl << endl;
	//cout << featureTestMatrix << endl << endl;

	//cout << featureTrainMatrix.rows << endl;
	//cout << featureTestMatrix.rows << endl;

//	safeFile(featureTestMatrix, "TrainMatrix");
//	safeFile(featureTrainMatrix, "TestMatrix");


	/************************************SVM*****************************************************************/
	/********************************************************************************************************/
	trainSVM(featureTrainMatrix, trainLableMat);
	//cout << testSVM(featureTestMatrix) << endl;

	Mat result = testSVM(featureTestMatrix);
	int numberOfRightResults = 0;
	float percentageRightResult;
	for (int i = 0; i < result.rows ; i++)
	{
		if (result.at<float>(i) == testLableMat.at<float>(i)) {
			numberOfRightResults++;
		}
	}

	percentageRightResult = (float)numberOfRightResults / (float)result.rows;
	cout << endl << endl << "Result in percent: " << percentageRightResult * 100 << "%" << endl;

	Mat m1 = Mat(1, 1, CV_64F, cvScalar(0.));
	imshow("Padding", m1);
	waitKey();

	return 0;
}